scriptData.script:
if (game.user.isUniqueGM)
{
    let fatigued = args.actor.hasCondition("fatigued")
    if (!fatigued)
    {
        args.actor.addCondition("fatigued")
        ui.notifications.notify("Fatiga añadida a " + args.actor.name + " que no puede ser quitada hasta que el síntoma Malestar se haya ido.")
    }
}

if (game.user.isUniqueGM)
{
    let fatigued = args.actor.hasCondition("fatigued")
    if (!fatigued)
    {
        args.actor.addCondition("fatigued")
        ui.notifications.notify("Fatiga añadida a " + args.actor.name + " que no puede ser quitada hasta que el síntoma Malestar se haya ido.")
    }
}

let difficulty = ""
if (this.effect.name.includes("Moderado"))
    difficulty = "easy"
else if (this.effect.name.includes("Grave"))
    difficulty = "average"
else
    difficulty = "veasy"

if (this.actor.isOwner)
{
    args.actor.setupSkill(game.i18n.localize("NAME.Endurance"), {context : {failure : args.actor.name + " muere por el Decaimiento"}, fields: {difficulty}, skipTargets: true, appendTitle : " - Decaimiento"}).then(setupData => {
        args.actor.basicTest(setupData).then(test => 
            {
                if (test.failed)
                    args.actor.addCondition("dead")
            })
        })
}

let modifier = 0
if (this.effect.name.includes("Moderado"))
    modifier = -20
else
    modifier = -10

let applicableCharacteristics = ["ws", "bs", "s", "ag", "t", "dex"]
if (args.type == "weapon")
    args.prefillModifiers.modifier += modifier
else if (args.type == "characteristic")
{
    if (applicableCharacteristics.includes(args.item))
        args.prefillModifiers.modifier += modifier
}
else if (args.type == "skill")
{
    if (applicableCharacteristics.includes(args.item.characteristic.key))
        args.prefillModifiers.modifier += modifier
}

if (game.user.isUniqueGM)
{
    let fatigued = args.actor.hasCondition("fatigued")
    if (!fatigued)
    {
        args.actor.addCondition("fatigued")
        ui.notifications.notify("Fatiga añadida a " + args.actor.name + " que no puede ser quitada hasta que el síntoma Malestar se haya ido.")
    }
}
